mapper.update:
update biz_brand
set is_deleted = 1
where brand_id = #{brandId,jdbcType=VARCHAR}

update biz_brand
set brand_name = #{brandName,jdbcType=VARCHAR},
  creat_time = #{creatTime,jdbcType=TIMESTAMP},
  update_time = #{updateTime,jdbcType=TIMESTAMP},
  is_deleted = #{isDeleted,jdbcType=BIT}
where brand_id = #{brandId,jdbcType=VARCHAR}
