mapper.select:
select

from biz_order
where is_deleted = '0'

    and user_id =#{userId}

        and (customer_name like CONCAT('%',#{kw},'%') or order_id = #{kw})


        and order_state = #{state}




    and 1=1

        and (customer_name like CONCAT('%',#{kw},'%') or order_id = #{kw})


        and order_state = #{state}


ORDER BY start_time DESC
