mapper.update:
update sys_user
set is_deleted = 1
where user_id = #{userId,jdbcType=VARCHAR}

mapper.insert:
insert into sys_user (user_name, user_password, user_account, 
  user_tel, user_sex, role_id, 
  creat_time, update_time, is_deleted
  )
values (#{userName,jdbcType=VARCHAR}, #{userPassword,jdbcType=VARCHAR}, #{userAccount,jdbcType=VARCHAR}, 
  #{userTel,jdbcType=CHAR}, #{userSex,jdbcType=CHAR}, #{roleId,jdbcType=VARCHAR}, 
  #{creatTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{isDeleted,jdbcType=BIT}
  )
